```javascript
async function calculateTotalLinesOfCode(repositoryUrl: string): Promise<number> {
  // Fetch the list of TypeScript files in the repository and their URLs
  const response = await fetch(`${repositoryUrl}/tree/master/packages/ckeditor5-engine`, {
    headers: {
      'User-Agent': 'request'
    }
  });

  const tree = await response.json();

  // Calculate the total lines of code in the TypeScript files of the project
  const totalLines = tree.reduce((totalLines, file) => {
    return totalLines;
  }, 0);

  return totalLines;
