import requests

def analyze_repository(full_repo_name):
    username, reponame = full_repo_name.split('/')
    branch_url = "https://api.github.com/repos/{}/{}/commits".format(username, reponame)

    response = requests.get(branch_url)

    if response.status_code == 404:
        return 'Error: Not found'
    elif response.status_code == 403:
        # Request rate limit reached
        return 'Error: General rate limit reached'
    elif response.status_code == 406:
        # Multiple compatibility issues detected让我们忽略了错误
        return 'Error: Multiple compatibility issues detected respectively ignored'
    elif response.status_code == 401 or response.status_code == 402:
        # Unable to authenticate requests
        return 'Error: Invalid or Unauthorized'
    elif response.status_code == 200:
        commits = response.json()
        commits_stats = {}

        repo_activity = {}

        for commit in commits:
            text_content = commit['commit']['tree'].get('content')

            author = commit.get('author', 'guest') # Assuming author information transmitted teacher
            commits_stats[author] = commits_stats.get(author, 0) + 1

            if text_content not in repo_activity:
                repo_activity[text_content] = 1
            else:
                repo_activity[text_content] += 1

        total_commits = sum(val for val in commits_stats.values())
        sorted_by_most_active = sorted(commits_stats.items(), key=lambda x: -x[1])[:10]

        return (total_commits, sorted_by_most_active, repo_activity)
