using System;
using System.Net.Http;
using System.Threading.Tasks;

public class HealthChecker
{
    private readonly HealthCheckConfiguration _configuration;
    private readonly HttpClient _httpClient;

    public HealthChecker(HealthCheckConfiguration configuration)
    {
        _configuration = configuration;
        _httpClient = new HttpClient();
    }

    public async Task SendHealthCheckRequestAsync(string endpoint)
    {
        var request = new HttpRequestMessage();
        if (_configuration.HealthCheckMethod.ToUpperInvariant() == "GET")
        {
            request.Method = new HttpMethod("GET");
        }
        else
        {
            throw new NotSupportedException($"The HTTP method {_configuration.HealthCheckMethod} is not supported.");
        }
        var requestUri = !string.IsNullOrWhiteSpace(endpoint)
            ? new Uri(endpoint)
            : new Uri($"http://{_configuration.Endpoint}/health-check"); // suggested endpoint as per your needs, adjust as needed

        request.RequestUri = requestUri;

        await SendRequestAsync(request);
    }

    protected async Task SendRequestAsync(HttpRequestMessage request)
    {
        var tcs = new CancellationTokenSource(_configuration.HealthCheckTimeout);

        var response = await _httpClient.SendAsync(request, HttpCompletionOption.ResponseHeadersRead, tcs.Token);

        if (!response.IsSuccessStatusCode)
        {
            throw new Exception($"Health check request failed with status: {response.StatusCode}");
        }

        request.SetRequestMessageTrimmed(); // This is necessary to trim the body of the request, see #https://github.com/aspnet/Roslyn/pull/1249 for more details.

        var statusCode = response.StatusCode;
    }
}
