import re
from typing import Dict, List, Tuple

def validate_package_metadata(metadata: Dict) -> Tuple[bool, List[str]]:
    regexes = {
        'name': r'^[a-z0-9_]+$',
        'version': r'^(\d+)\.(\d+)\.(\d+)$',  # semantic versioning format
    }

    valid_params = {'name', 'version'}
    if not isinstance(metadata, dict) or not set(metadata.keys()) == valid_params:
        return False, ["Invalid dictionary: provided object does not have all required keys"]

    for name in regexes:
        if name not in metadata:
            continue
        if not re.match(regexes[name], metadata[name]):
            return False, ["Invalid '{}': {}".format(name, metadata[name])]

    if metadata['version'] and not re.match(regexes['version'], metadata['version']):
        return False, ["Invalid version: {}".format(metadata['version'])]

    return True, []
