class Node:
    def __init__(self, value):
        self.value = value
        self.left = None
        self.right = None

class BinarySearchTree:
    def __init__(self):
        self.root = None

    def insert(self, value):
        self.root = self.insert_rec(self.root, value)

    def insert_rec(self, root, value):
        if root is None:
            return Node(value)

        if value < root.value:
            root.left = self.insert_rec(root.left, value)
        else:
            root.right = self.insert_rec(root.right, value)

        return root

    def delete(self, value):
        self.root = self.delete_node(self.root, value)

    def delete_node(self, root, value):

        if root is None:
            return root

        if value < root.value:
            root.left = self.delete_node(root.left, value)
        elif(value > root.value):
            root.right = self.delete_node(root.right, value)
        else:
            if root.left is None:
                temp = root.right
                root = None
                return temp
            elif root.right is None:
                temp = root.left
                root = None
                return temp
        tmp = None
        if value < root.value:
            tmp = self.findMinValueNode(root.left)
        else:
            tmp = self.findMaxValueNode(root.right)
        temp.right = root.right
        temp.left = root.left
        root = None
        return tmp

    def inorderPrint(self, root):
        if root is not None:
            self.inorderPrint(root.left)
            print(root.value)
            self.inorderPrint(root.right)

    def search(self, value):
        return self.search_rec(value)

    def search_rec(self, value):
        if self.root is None or self.root.value == value:
            return (self.root is not None)
        return (value < self.root.value) and self.search_rec(value, self.root.left) or self.search_rec(value, self.root.right)

    def calculateDepth(self, root):
        if root is None:
            return 0
        return max(self.calculateDepth(root.left), 
                    self.calculateDepth(root.right)) + 1

    def height(self):
        return self.calculateDepth(self.root) - 1

