def build_URL(loginURL, loginText=None):

    import urllib
    from urlparse import urlparse, parse_qsl

    if loginURL != None:
        p = urlparse(loginURL).path

        query = urllib.urlencode({
            "sourceUrl": p,
            "loginText": loginText
        })

        return urlparse(loginURL).scheme + "://" + urlparse(loginURL).netloc + "?" + query

    return None


