# Split the system_IPAdress_Data into individual parts
system_IPAdress_parts = system_IPAddress_Data.split(".")

# Initialize variables to store the resulting values
system_IPAddress = ""
system_Key = ""

# Iterate over the parts of the system_IPAddress_Data
for part in system_IPAdress_parts:
    # Convert the part to an integer and handle any errors
    try:
        part_int = int(part)

        # Check if the part is within the valid range for an IP address
        if part_int < 0 or part_int > 255:
            return None, None, "Invalid IP address part: {}".format(part)

        # Convert the part to an octal string
        part_octal_str = oct(part_int)[2:]

        # Append the octal part to the system_IPAddress string
        system_IPAddress += part_octal_str + "."
    except ValueError:
        return None, None, "Invalid IP address part: {}".format(part)

# Remove the trailing dot from the system_IPAddress string
system_IPAddress = system_IPAddress.rstrip(".")

# Iterate over the system_Key_Info dictionary
for key, value in system_Key_Info.items():
    # Convert the value to a string and handle any errors
    try:
        value_str = str(value)

        # Check if the value is within the valid range for a system_Key_Info key
        if len(key) != len(value_str):
            return None, None, "Invalid system_Key_Info key and value length: {}-{}".format(key, value)

        # Append the key-value pair to the system_Key string
        system_Key += "{}-{}.".format(key, value)
    except ValueError:
        return None, None, "Invalid system_Key_Info key and value: {}-{}".format(key, value)

# Remove the trailing dot from the system_Key string
system_Key = system_Key.rstrip(".")

# Return the resulting IP address and system_Key
return system_IPAddress, system_Key, ""

