import re
def isValidEmailAddress(emailAddressCandidate):
    charset = {"@":None,".":None}
    for key, value in charset.items():
        if emailAddressCandidate.count(key) > 1:
            return False
    pattern = r'\w+'
    if re.match(pattern, emailAddressCandidate) == None:
        return False
    ok = 0
    curChar = 0
    for char in system_Name_Info:
        if char == "@":
            ok = 1
        elif char == ".":
            ok = 2
        if ok != 0 and ok != 1:
            if curChar != 0:
                pattern = r'\w+'
                ok = 0
                if re.match(pattern, char) == None:
                    return False
            else:
                pattern = r'\w+'
                if re.match(pattern, char) == None:
                    return False
        elif ok == 1 and char != "@":
            pattern = r'\w+'
            if re.match(pattern, char) == None:
                return False
        elif ok == 2 and char != ".":
            pattern = r'\w+'
            if re.match(pattern, char) == None:
                return False
        curChar = 1
    return ok == 2
