class GenerateClientQueue:
    def __init__(self, queue_size):
        self.queue_size = queue_size
        self.queue = []  # [queue_size]

    def enqueue(self, item):
        """enqueue - add item to the end of the queue"""
        if len(self.queue) < self.queue_size:
            self.queue.append(item)

    def dequeue(self):
        """dequeue - remove item from the beginning of the queue and return it"""
        return self.queue.pop(0)


class GenerateServerQueue:
    """A server queue that holds jobs.
    A server queue can't hold more than the max number of jobs.
    The server queue has to have some jobs to run.
    Jobs are removed from the server queue in the order they are added.
    """

    def __init__(self, queue_size):
        self.queue_size = queue_size
        self.queue = []  # [queue_size]

    def enqueue(self, item):
        """enqueue - add item to the end of the queue"""
        if len(self.queue) < self.queue_size:
            self.queue.append(item)

    def dequeue(self):
        """dequeue - remove item from the beginning of the queue and return it"""
        return self.queue.pop(0)

