    import re
    # Define the regular expressions for email and password
    email_regex = r'\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Z|a-z]{2,}\b'
    password_regex = r'\b[A-Za-z0-9@#]{8,}\b'

    # Define the dictionary for mapping email and password errors
    error_messages = {
        'InvalidEmail': 'Please enter a valid Email.',
        'InvalidPassword': 'Please enter a valid Password.'
    }

    # Define a function to validate the email
    def validate_email(email):
        if len(email) > 50:
            return None, "Email should not be exceeding 50 characters"
        elif not re.search(email_regex, email):
            return None, error_messages['InvalidEmail']
        else:
            return email, None

    # Define a function to validate the password
    def validate_password(password):
        if len(password) < 8:
            return None, "Password should be at least 8 characters"
        elif not any(char.isdigit() for char in password):
            return None, "Password should contain at least one digit"
        elif not any(char.isalpha() for char in password):
            return None, "Password should contain at least one letter"
        elif not any(char in "!@#$%^&*()_+-=[]{}|;':,.<>/?`~" for char in password):
            return None, "Password should contain at least one special character"
        else:
            return password, None

    # Validate the email
