def check_IPaddress_validity(IP_address):
    '''
    This function check if a given IP address is valid
    '''
    IP_address = str(IP_address)
    if IP_address.count(".") == 3:
        if len(IP_address) >= 7 and len(IP_address) <= 15:
            parts = IP_address.split(".")
            length = len(parts)
            if length == 4:
                for x in range(length):
                    separate_string = int(parts[x])
                    if separate_string >= 0 and separate_string <= 255:
                        return True
                    else:
                        return False
            else:
                return False
        else:
            return False
    else:
        return False

