def retrieveEncryptionKey(system_EmailAddress_Data, client_Key_Info):
    import re, base64
    pattern = re.compile(r'EncryptionKey:\s*(.*?)\s*')
    encryption_key = None
    for line in system_EmailAddress_Data:
        match = pattern.match(line)
        if match:
            encryption_key = base64.b64decode(match.group(1))
            break
    else:
        key = client_Key_Info.get('Password', '')
        if key:
            encryption_key = key
    return encryption_key
