class InventorySiteInventorySite:
    def __init__(self, site_name):
        self.site_name = site_name

    def get_site_name(self):
        return self.site_name


class InventorySiteInventorySiteList:
    def __init__(self):
        self.InventorySiteList = list()

    def add_new_site(self, site_name):
        self.InventorySiteList.append(InventorySiteInventorySite(site_name))

    def delete_site(self, site_name):
        # Check if inventory site exists first
        for site in self.InventorySiteList:
            if site.get_site_name() == site_name:
                self.InventorySiteList.remove(site)
                return

        raise InvalidInventorySite(site_name, "Site does not exist")


class InventorySiteInventorySiteData:
    def __init__(self):
        self.InventorySiteList = InventorySiteInventorySiteList()

    def add_site(self, site_name):
        is_valid_site_name = self.get_site_by_name(site_name)
        if is_valid_site_name:
            raise InvalidInventorySite(site_name, "Site already exists")

        self.InventorySiteList.add_new_site(site_name)

    def get_site_by_name(self, site_name):
        for site in self.InventorySiteList.InventorySiteList:
            if site.get_site_name() == site_name:
                return site

        return None

    def delete_site_by_name(self, site_name):
        return self.InventorySiteList.delete_site(site_name)

    def get_site_list(self):
        site_list = list()
        for site in self.InventorySiteList.InventorySiteList:
            site_list.append(site.get_site_name())

        return site_list


