def generate_KeyAndPassword():
    key_info_key = input("Input the key for the encryption/decryption algorithm:")
    key_info_alphabet = input("Input the alphabet for the encryption/decryption algorithm (example: abcdefghijklmnopqrstuvwxyz)")
    key_info_length = input("Input the length of the key (ideally should be the same length as the alphabet)")
    return key_info_key, key_info_alphabet, key_info_length


def load_KeyAndPassword(system_assword_data):
    key_info_key = system_assword_data[0]
    key_info_alphabet = system_assword_data[1]
    key_info_length = system_assword_data[2]
    return key_info_key, key_info_alphabet, key_info_length


def encryption(plaintext, key_info_key, key_info_alphabet):
    ciphertext = ""
    for letter in plaintext:
        position = key_info_alphabet.index(letter)
        new_position = position + int(key_info_key)
        if new_position > len(key_info_alphabet):
            new_position -= len(key_info_alphabet)
        ciphertext += key_info_alphabet[new_position]
    return ciphertext


def decryption(ciphertext, key_info_key, key_info_alphabet):
    plaintext = ""
    for letter in ciphertext:
        position = key_info_alphabet.index(letter)
        new_position = position - int(key_info_key)
        if new_position < 0:
            new_position += len(key_info_alphabet)
        plaintext += key_info_alphabet[new_position]
    return plaintext


