def additionTwoNumbers(addition_Data):
    """This function get two numbers and add them together
    and return the result"""
    result = addition_Data[0] + addition_Data[1]
    return result

def subtractionTwoNumbers(subtraction_Data):
    """This function get two numbers and subtract them
    and return the result"""
    result = subtraction_Data[0] - subtraction_Data[1]
    return result

def multiplicationTwoNumbers(multiplication_Data):
    """This function get two numbers and multiply them together
    and return the result"""
    result = multiplication_Data[0] * multiplication_Data[1]
    return result

def divisionTwoNumbers(division_Data):
    """This function get two numbers and divide them
    and return the result"""
