        password = password.replace('?', '1')
    elif '!' in password:
        password = password.replace('!', '2')
    elif '@' in password:
        password = password.replace('@', '3')
    elif '#' in password:
        password = password.replace('#', '4')
    elif '$' in password:
        password = password.replace('$', '5')


