def validate_IPAddress(IPaddress):
    # check for IPv4
    if IPaddress.startswith("IPv4:"):
        IPv4 = IPaddress[5:]
        try:
            octets = IPv4.split(".")
            if len(octets) != 4:
                return False
            for octet in octets:
                if not (0 <= int(octet) <= 255):
                    return False
        except:
            return False
        return True
    # check for IPv6
    if IPaddress.startswith("IPv6:"):
        IPv6 = IPaddress[5:]
        if IPv6.count(":") != 7:
            return False
        for octet in IPv6.split(":"):
            if not (0 <= int(octet, 16) <= 65535):
                return False
        return True
    return False


def validate_system_name(system_Name):
    if len(system_Name) > 20:
        return False
    if system_Name.startswith("www."):
        return False
    if system_Name.lower() in ["localhost", "localhost.localdomain"]:
        return False
    return True


