def is_valid_IPAddress(ip):
    # Check if the IP address is in the correct format
    if not "." in ip:
        return False
    # Split the IP address into its four octets
    octets = ip.split(".")
    # Check if all octets are valid integers between 0 and 255
    for octet in octets:
        if not (0 <= int(octet) <= 255):
            return False
    return True

