# Generate a password with the given number of characters
def generate_password(n: int, characters: str) -> str:
    import random
    import string

    alphabet = list(string.ascii_letters + string.digits)
    password = ''.join(random.choice(alphabet) for _ in range(n))
    while any(c in password for c in characters):
        password = "".join([c if c not in characters else random.choice(alphabet) for c in password])
    return password

