def validate_IPAddress(ip_address):
    if ip_address == " ":
        return "Not-Valid-IP"
    if ip_address[0] == ".":
        return "Not-Valid-IP"
    ip_address_Split = ip_address.split(".")
    if len(ip_address_Split) != 4:
        return "Not-Valid-IP"
    for octet in ip_address_Split:
        if not octet.isdigit():
            return "Not-Valid-IP"
        if int(octet) < 0 or int(octet) > 255:
            return "Not-Valid-IP"
    return "Valid-IP"


