import re
def validIPAddress(IP):
    # Regex to check valid IP for IPv4 and IPv6
    regex = "^((25[0-5]|2[0-4][0-9]|1[0-9][0-9]|[1-9]?[0-9])\.){3}(25[0-5]|2[0-4][0-9]|1[0-9][0-9]|[1-9]?[0-9])$"
    # Compile the ReGex
    p = re.compile(regex)
    # If the string is empty
    # return false
    if len(IP) == 0:
        return False
    # Return if the string
    # matched the ReGex
    if re.search(p, IP):
        return True
    else:
        return False
def validKeyInfo(key_info):
    import re
    regex = "^([A-Fa-f0-9]{4,20}:){1,7}[A-Fa-f0-9]{1,4}$"
    p = re.compile(regex)
    if re.search(p, key_info):
        return True
    else:
        return False
