def check_for_invalid_IPv4(ipv4_address, version):
    ipv4_address = list(ipv4_address.split('.'))
    if len(ipv4_address) != 4:
        return version
    for octet in ipv4_address:
        if not 0 <= int(octet) <= 255:
            return version
    return version


def check_for_invalid_IPv6(ipv6_address, version):
    ipv6_address = list(ipv6_address.split(':'))
    if len(ipv6_address) != 8:
        return version
    for octet in ipv6_address:
        if len(octet) > 4 or not octet:
            return version
        for c in octet:
            if not c.isdigit() and not c.isalpha() and not c == '-':
                return version
    return version

