    '''
    This function is used to check if the input that is an account information is valid.
    '''

    if len(input_IPAddress) != 4:
        return False

    for IP in input_IPAddress:
        if not str.isdigit(IP):
            return False

        if int(IP) < 0 or int(IP) > 254:
            return False

    return True

