def check_frequency(arr, num):
    frequency = 0
    for i in arr:
        if i == num:
            frequency += 1
    return frequency


def check_prime(num):
    if num > 1:
        for i in range(2, num):
            if num % i == 0:
                return False
        return True


def check_Armstrong(num):
    temp = num
    sum = 0
    while temp > 0:
        digit = temp % 10
        sum += digit ** 3
        temp //= 10
    if sum == num:
        return True
    else:
        return False


def check_even_odd(num):
    if num % 2 == 0:
        return True
    else:
        return False


def check_Palindrome(num):
    temp = num
    reverse = 0
    while temp > 0:
        digit = temp % 10
        reverse = (reverse * 10) + digit
        temp //= 10
    if reverse == num:
        return True
    else:
        return False


def check_Perfect(num):
    sum = 0
    for i in range(1, num):
        if num % i == 0:
            sum += i
    if sum == num:
        return True

