def binary_search_by_name(account_EmailAddress_Details, system_IPAddress_Info, start, finish, name):
    listRange = finish - start
    if listRange == 0:
        if account_EmailAddress_Details[start].split(' | ')[0].lower() == name.lower():
            return start
        else:
            return None
    elif listRange == 1:
        if account_EmailAddress_Details[start].split(' | ')[0].lower() == name.lower():
            return start
        elif account_EmailAddress_Details[start + 1].split(' | ')[0].lower() == name.lower():
            return start + 1
        else:
            return None
    else:
        middle = int((start + finish) / 2)

        if account_EmailAddress_Details[middle].split(' | ')[0].lower() == name.lower():
            return middle
        elif account_EmailAddress_Details[middle - 1].split(' | ')[0].lower() == name.lower():
            return middle - 1
        elif account_EmailAddress_Details[middle + 1].split(' | ')[0].lower() == name.lower():
            return middle + 1
        elif account_EmailAddress_Details[middle].split(' | ')[0].lower() > name.lower():
            return binary_search_by_name(account_EmailAddress_Details, system_IPAddress_Info, start, middle - 1, name)
        elif account_EmailAddress_Details[middle].split(' | ')[0].lower() < name.lower():
            return binary_search_by_name(account_EmailAddress_Details, system_IPAddress_Info, middle + 1, finish, name)
        else:
            return None


