    # This function will generate and return a list of candidate IP addresses
    # client_IPAddress_Info: IP address or IP address list (ex: ["192.168.1.1", "192.168.10.10"])
    ipAddressList = []

    if isinstance(client_IPAddress_Info, str):
        # Check if the input is a single IP address
        if not client_IPAddress_Info or "." not in client_IPAddress_Info:
            raise ValueError("The input IP address is not valid.")

        client_IPAddress = client_IPAddress_Info.replace(".", "")
        # Extract the first 25 bits from the hexadecimal representation of the IP address
        first_25_bits = client_IPAddress[:25]
        # Generate the IP addresses by incrementing the first 25 bits
        for i in range(256):
            new_ip_address = first_25_bits + str(i) + ".0.0"
            ipAddressList.append(new_ip_address)

    elif isinstance(client_IPAddress_Info, list):
        # Check if the input is a list of IP addresses
        for ipAddress in client_IPAddress_Info:
            if ipAddress.startswith("10."):
                ipAddressList.append(ipAddress)
            elif ipAddress.startswith("192.168."):
                ipAddressList.append(ipAddress)
            elif ipAddress.startswith("172.16."):
                ipAddressList.append(ipAddress)
            else:
                raise ValueError("The input IP address is not valid.")
    else:
        raise ValueError("The input IP address is not valid.")

    # Sort the IP addresses in ascending order
    ipAddressList.sort()
    return ipAddressList


