def calculating_IP(user_IPAddress_Data):
    IPaddress_list = user_IPAddress_Data.split('.')
    new_list = []
    for IP in IPaddress_list:
        new_list.append(int(IP))
    new_list = list(reversed(new_list))
    octet_value = 0
    for IP in new_list:
        octet_value = octet_value + int(IP)
    octet_value = octet_value / 255
    octet_value = int(octet_value)
    return octet_value


def get_subnet_mask(user_IPAddress_Data):
    IPaddress_list = user_IPAddress_Data.split('.')
    new_list = []
    for IP in IPaddress_list:
        new_list.append(int(IP))
    new_list = list(reversed(new_list))
    octet_value = 0
    for IP in new_list:
        octet_value = octet_value + int(IP)
    octet_value = octet_value / 255
    octet_value = int(octet_value)
    if octet_value == 0:
        print("Your subnet mask is /0")
    elif octet_value == 128:
        print("Your subnet mask is /1")
    elif octet_value == 192:
        print("Your subnet mask is /2")
    elif octet_value == 224:
        print("Your subnet mask is /3")
    elif octet_value == 240:
        print("Your subnet mask is /4")
    elif octet_value == 248:
