    """Validates if password is in the correct format"""
    if len(password) > 30:
        return False
    to_upper = password.upper()
    if to_upper == password:
        return False
    to_lower = password.lower()
    if to_lower == password:
        return False
    to_upper_or_lower = password.upper() or password.lower()
    if to_upper_or_lower == password:
        return False
    if " " in password:
