            check = input('>> Please enter Y or N: ')
            if check == 'Y':
                user_Name_Data.pop(i)
                account_Username_Credential.pop(j)
                print('\n\t\t\t\tPlease confirm that you wish to delete {}'.format(account_Username_Credential[j]))
                return user_Name_Data, account_Username_Credential
                break

            elif check == 'N':
                print('\n\t\t\t\tPlease confirm that you wish to delete {}'
                      .format(account_Username_Credential[j]))
                return user_Name_Data, account_Username_Credential
                break
            else:
                print('\n\t\t\t\tPlease enter Y or N.')
    except IndexError:
        print('\n\t\t\t\tSorry! you can\'t delete {} because it doesn\'t exist'.format(check_Names))
        return user_Name_Data, account_Username_Credential
