    if Password == '':
        return "Password can't be empty."
    if Repeat_password == '':
        return "Repeat password can't be empty."
    if len(Password) < 6:
        return "Password length must be at least 6 charachters"
    if len(Password) > 20:
        return "Password length must be at least 6 charachters"
    if not any(char.isdigit() for char in Password):
        return "Password must contain at least one number"
    if not any(char.isupper() for char in Password):
        return "Password must contain at least one upper letter"
    if not any(char.islower() for char in Password):
        return "Password must contain at least one lower letter"
    if Password != Repeat_password:
        return "Passwords are not the same. Please try again."


