def calculator():
    print("Welcome to my calculator")
    print("Select a number from the list, then select another number")
    number1 = float(input("Enter a number "))
    number2 = float(input("Enter a number "))

    print("\n", number1)
    print("\n", number2)

    print("\n", number1, "+", number2, " = ", number1 + number2)
    print(number1, "-", number2, " = ", number1 - number2)
    print(number1, "*", number2, " = ", number1 * number2)
    print(number1, "/", number2, " = ", number1 / number2)
    print(number1, "%", number2, " = ", number1 % number2)


