def decrypt(my_Password):
    for i in range(len(my_Password)):
        if my_Password[i] == '_' and i%2 == 0:
            my_Password[i] = my_Password[i-1] + my_Password[i-1]
            i += 2
        elif my_Password[i] == '_' and i%2 != 0:
            my_Password[i] = my_Password[i-1] + my_Password[i-1]
            i += 2
        else:
            pass
    return my_Password

