def get_network_address_and_broadcast_address(ipAddress, subnet_mask):
    network_address = int(ipAddress) & int(subnet_mask)
    broadcast_address = int(ipAddress) | (0xffffffff ^ int(subnet_mask))
    network_address = str(network_address)
    broadcast_address = str(broadcast_address)
    return network_address, broadcast_address


