def generate_IP_Address_Regex(ipAddressRanges):
    ipAddressRegex = []
    for ipAddressRange in ipAddressRanges:
        startIP, endIP = ipAddressRange.split("-")
        startIP = startIP.split(".")
        endIP = endIP.split(".")
        for address in range(int(startIP[0]), int(endIP[0])+1):
            for ip in range(int(startIP[1]), int(endIP[1])+1):
                for ip in range(int(startIP[2]), int(endIP[2])+1):
                    for ip in range(int(startIP[3]), int(endIP[3])+1):
                        ipAddressRegex.append(
                            str(address)+"."+str(ip+1)+"."+str(ip)+"."+str(ip))
    return ipAddressRegex

