def isIPv4Address(address):
    """
    Checks whether a string is IPv4 address or not.
    Returns:
        bool
    """
    parts = address.split('.')
    if len(parts) != 4:
        return False
    try:
        return all((0 <= int(part) <= 255 for part in parts))
    except ValueError:
        return False

def isIPv6Address(address):
    """
    Checks whether a string is IPv6 address or not.
    Returns:
        bool
    """
    parts = address.split(':')
    if len(parts) != 8:
        return False
    try:
        return all((0 <= int(part, 16) <= 65535 for part in parts))
    except ValueError:
        return False

