def validate_PasswordConfirmPassword(passwordConfirmation, password):
    if passwordConfirmation != password:
        return 'Password Confirmation Does not Match Password'
    else:
        return 'Valid Password Confirmation'

def validate_Password():
    passwordData = validate_PasswordLength(password)
    passwordData += validate_PasswordCharacter(password)
    passwordData += validate_PasswordConfirmPassword(passwordConfirmation, password)
    return passwordData

