def check_IPaddress(ip_address):
    """
    This function checks if an IP address is valid.
    :param ip_address: A string containing the IP address to be validated.
    :type ip_address: str
    :return: True if the IP address is valid, False otherwise.
    :rtype: bool
    """

    # Check if the IP address is in the correct format
    if len(ip_address.split('.')) != 4:
        return False

    # Check if each octet is within the range of 0 to 255
    for octet in ip_address.split('.'):
        if not octet.isdigit() or int(octet) < 0 or int(octet) > 255:
            return False

    return True

