def break_key_to_tokens(user_Key_Data):
    token1 = user_Key_Data[0:2] + user_Key_Data[2:3]
    token2 = user_Key_Data[3:5] + user_Key_Data[5:7]
    token3 = user_Key_Data[7:9] + user_Key_Data[9:11]
    token4 = user_Key_Data[11:13] + user_Key_Data[13:15]
    return token1, token2, token3, token4


def process_data_if_ip_address(ip_address, user_key_Data, break_key_to_tokens):
    """
    :param ip_address: This is string IP address of client
    :param user_key_Data: This is string coming from client
    :param break_key_to_tokens: This is function to break the key to tokens
    :return processd_data
    """
    inputs = ip_address + user_key_Data
    if len(inputs) < 16:
        print "Error: Input is too short! Not enough characters."
        return None
    token1, token2, token3, token4 = break_key_to_tokens(user_key_Data)
    processd_data = inputs + token1 + token2 + token3 + token4
    if len(processd_data) > 16:
        processd_data = inputs + token1 + token2 + token3 + token4[0:1] + token4[1:]
    else:
        processd_data = inputs + token1 + token2 + token3 + token4
    return processd_data


