# Function to validate IP address version
def validate_ip_address_version(ip_address):
    ip_version = "Unknown"

    # Check version 4
    if ip_address.count(".") == 3:
        try:
            ip_address_int = int(ip_address.split(".")[0])
            if ip_address_int < 0 or ip_address_int > 255:
                raise ValueError("Invalid IP address")
            ip_version = "IPv4"

        except ValueError:
            print("Invalid IP address")

    # Check version 6
    elif ip_address.count(":") == 7 and ip_address.count(".") == 0:
        try:
            ip_address_int = int(ip_address.split(":")[0])
            if ip_address_int < 0 or ip_address_int > 65535:
                raise ValueError("Invalid IP address")
            ip_version = "IPv6"

        except ValueError:
            print("Invalid IP address")

    return ip_version


# Function to check if IP address is valid
def is_valid_ip_address(ip_address):
    ip_parts = ip_address.split(".")
    if len(ip_parts) != 4 or any(part.startswith("::") for part in ip_parts):
        return False

    for part in ip_parts:
        if not part.isdigit():
            return False

    return True


