def encrypt(user_input, key):
    """
    This function will encrypt the user input with the key given
    """
    if type(key) is str:
        return encrypt(user_input, int(key) % 26)
    else:
        user_input = str(user_input)
        encrypted_input = []
        for character in user_input:
            encrypted_input.append(chr(ord(character) + key))
        return ''.join(encrypted_input)

def decrypt(user_input, key):
    """
    This function will decrypt the user input with the key given
    """
    if type(key) is str:
        return decrypt(user_input, int(key) % 26)
    else:
        user_input = str(user_input)
        decrypted_input = []
        for character in user_input:
            decrypted_input.append(chr(ord(character) - key))
        return ''.join(decrypted_input)


