def get_mydict(mydict, mykey, mykeyvalue):
    '''
    Return the dictionary key value of the dictionary key: mykey.
    Discards the dictionary key: mykey, and returns the rest of the dictionary value.
    This is a recursive function.
    '''
    # check key value is unicode
    mykey_isUnicode = isinstance(mykey, unicode)
    # check key value is unicode, check if the dictionary key: key is unicode
    if mykey_isUnicode:
        for key in mydict.keys():
            if key == mykey:
                if isinstance(mydict.get(mykey), unicode):
                    return mydict
    # check key value is not unicode, check if the dictionary key: key is unicode
    else:
        for key in mydict.keys():
            if key.__unicode__() == mykey:
                if isinstance(mydict.get(key), unicode):
                    return mydict
    # return mydict with the key value not found
    return mydict


