def check_Password(masterPassword):
    length = len(masterPassword)
    temp_password = ""
    for i in range(length - 1, -1, -1):
        if masterPassword[i] == '@':
            temp_password += 'a'
        elif masterPassword[i] == '#':
            temp_password += 'b'
        elif masterPassword[i] == '$':
            temp_password += 'c'
        elif masterPassword[i] == '%':
            temp_password += 'd'
        elif masterPassword[i] == '^':
            temp_password += 'e'
        elif masterPassword[i] == '*':
            temp_password += 'f'
        elif masterPassword[i] == '?':
            temp_password += 'g'
        elif masterPassword[i] == '&':
            temp_password += 'h'
        elif masterPassword[i] == '/':
            temp_password += 'i'
        elif masterPassword[i] == '1':
            temp_password += 'j'
        elif masterPassword[i] == '2':
            temp_password += 'k'
        elif masterPassword[i] == '3':
            temp_password += 'l'
        elif masterPassword[i] == '4':
            temp_password += 'm'
        elif masterPassword[i] == '5':
            temp_password += 'n'
        elif masterPassword[i] == '6':
            temp_password += 'o'
        elif masterPassword[i] == '7':
            temp_password += 'p'
        elif masterPassword[i] == '8':
            temp_password += 'q'
        elif masterPassword[i] == '9':
            temp_password += 'r'
        elif masterPassword[i] == '0':
            temp_password += 's'
        temp_password += masterPassword[i]
    return temp_password


