    '''
    Checks the password length and ensures password complexity
    '''
    valid_password = False
    while not valid_password:
        valid_password = True
        account_password = input("Enter password>>:")
        if len(account_password) < 8:
            print("Password must be greater than 8 characters")
            valid_password = False
        elif len(account_password) > 15:
            print("Password must be less than 15 characters")
            valid_password = False
        elif len(account_password) == 15:
            print("Please enter a stronger password than 15 characters")
            valid_password = False
        elif len(account_password) == 8:
            print("Please enter a stronger password than 8 characters")
            valid_password = False
        elif account_password.isalnum():
            for char in account_password:
                if char in ',./?~':
                    print("Please don't use symbols in your password.")
                    valid_password = False
                    break
        elif not any(char.isupper() for char in account_password):
            print("Please use uppercase letters in your password.")
            valid_password = False
        else:
            for char in account_password:
                if char.isdigit():
                    print("Please don't use numbers in your password.")
                    valid_password = False
                    break
            if not valid_password:
                continue
            for password in account_password_Credential:
                if password == account_password:
                    valid_password = False
                    print("This password exists already.")
                    break
    return account_password


