class Label:
    def __init__(self, text, font=('Sans-Serif', 16), width=None, x=0, y=0):
        if width == None:
            width = len(text) * 7
        label = tk.Label(text=text, font=font)
        label.configure(width=width, height=2, bg='black', anchor='w')
        label.place(x=x, y=y)
        self.label = label

    def configure(self, **kwargs):
        for key, value in kwargs.items():
            setattr(self.label, key, value)

class Entry:
    def __init__(self, text=''):
        entry = tk.Entry(font=('Arial', 12))
        entry.configure(width=len(text) * 11, justify='center', bd=2, relief=tk.SUNKEN)
        entry.insert(0, text)
        self.entry = entry
    def configure(self, **kwargs):
        for key, value in kwargs.items():
            setattr(self.entry, key, value)

