def encrypt(key, message):
  key = key.upper()
  key_dict = dict(zip(key, key))
  message = message.upper()
  encrypted = [(key_dict[char] if char in key_dict else char for char in message) for message in message]
  return "".join("".join(entry) for entry in zip(*encrypted))

def decrypt(key, message):
  key = key.upper()
  key_dict = dict(zip(key, key))
  key_dict_rev = {value: key for key, value in key_dict.items()}
  message = message.upper()
  decrypted = [(key_dict_rev[char] if char in key_dict_rev else char for char in message) for message in message]
  return "".join("".join(entry) for entry in zip(*decrypted))

