def domainname_check(client_IPAddress_Details):
    try:
        import dns.resolver
    except ImportError:
        print 'Please ensure python-dns-resolver package is installed'
        return None
    # Requires DNS resolvers available
    try:
        from IPy import IP
        for clientIPAddress in client_IPAddress_Details:
            ip = IP(clientIPAddress)
            if ip.iptype() == 'PRIVATE':
                return clientIPAddress
            elif ip.iptype() == 'PUBLIC':
                try:
                    for nameserver in ['8.8.8.8', '8.8.4.4']:
                        dns_resolver = dns.resolver.Resolver()
                        dns_resolver.nameservers = [nameserver]
                        try:
                            dns_resolver.query(clientIPAddress)
                            return clientIPAddress
                        except Exception as e:
                            continue
                except dns.resolver.NXDOMAIN:
                    continue
    except ImportError:
        print 'Please ensure python-dns-resolver package is installed'
        return None
    except Exception as e:
        print e
        return None


def geo_ip_lookup(client_IPAddress_Details):
    try:
        import geoip2.database
        reader = geoip2.database.Reader('GeoLite2-City.mmdb')
        for clientIPAddress in client_IPAddress_Details:
            try:
                response = reader.city(clientIPAddress)
                return response
            except Exception as e:
                print e
                continue
    except Exception as e:
        print e
        return None
    except ImportError:
        print 'Please ensure python-geoip2 package is installed'
        return None

