# convert subnet mask to binary
def subnet_to_binary(subnet_mask):
    bin_subnet_mask = subnet_mask.split('.')
    binary_subnet_mask = ''
    for x in bin_subnet_mask:
        y = bin(int(x))
        z = str(y).split('b')
        binary_subnet_mask += z[1]
    return binary_subnet_mask


