# generate alphabetic identifier
def generate_id(length=4, letters=None):
    # if letters is None:
    if letters is None:
        letters = string.ascii_letters
    id_list = list(itertools.product(letters, repeat=length))
    id_list = [''.join(i) for i in id_list]
    return id_list
# function to uppercase first letter
def capitalized_first_letter(string):
    if len(string) == 0:
        return string
    else:
        return string.title()
def generate_id_in_unicodedata(length):
    return unicodedata.name(generate_id(length)[0])
# created function to check if it's email
def is_email(email):
    # https://stackoverflow.com/questions/802253/regular-expression-to-match-valid-email-addresses
    # https://docs.python.org/2/library/re.html
    email = email.lower()
    if re.match(r"^[_a-z0-9-]+(\.[_a-z0-9-]+)*@[a-z0-9-]+(\.[a-z0-9-]+)*(\.[a-z]{2,4})$", email):
        return True
    else:
        return False
# https://stackoverflow.com/questions/15105020/determine-if-a-string-is-ascii-or-not
def is_ascii(s):
    return all(ord(c) < 128 for c in s)
