def validate_SYS_NetworkIP4(sys_nw_ip4): 
    ip4_check = sys_nw_ip4.split('.')
    if len(ip4_check) != 4:
        return 'Error: Invalid Number of IP Addresses Provided. Please provide 4 IP addresses.'
    for num in ip4_check:
        if not num.isdigit(): 
            return 'Error: Invalid IP Addresses Provided. Please provide all IP addresses in Numeric form.'
    return True

def validate_SYS_NetworkIP6(sys_nw_ip6):
    ip6_check = sys_nw_ip6.split(':')
    if len(ip6_check) != 8:
        return 'Error: Invalid Number of IP Addresses Provided. Please provide 8 IP addresses.'
    for num in ip6_check:
        if not num.isdigit() and not num.startswith('0'): 
            return 'Error: Invalid IP Addresses Provided. Please provide all IP addresses in either Numeric or Hexadecimal form.'
    return True

def validate_SYS_NetworkPortNums(sys_nw_portnums): 
    split_nums = sys_nw_portnums.split(',')
    for port_num in split_nums: 
        if int(port_num) < 0 or int(port_num) > 65535:
            return 'Error: Invalid Port Number Provided. Please provide Ports in Range 0 - 65535.'
    return True

