    if password_Data == '':
        print('Password is required')
        return False
    elif not re.search('[A-Z]', password_Data):
        print('At least one uppercase character')
        return False
    elif not re.search('[a-z]', password_Data):
        print('At least one lowercase character')
        return False
    elif not re.search('[0-9]', password_Data):
        print('At least one number')
        return False
    elif not re.search('[#@$&]', password_Data):
        print('At least one special character')
        return False
    elif len(password_Data) < 8:
        print('Password must be of 8 characters or more')
        return False
    else:
        return True

